mapper.select:
WITH RECURSIVE dept_tree AS ( SELECT id, parent_id FROM sys_dept WHERE id = #{deptId} AND deleted = 0
UNION ALL
SELECT d.id, d.parent_id FROM sys_dept d INNER JOIN dept_tree dt ON d.parent_id = dt.id WHERE d.deleted = 0 ) SELECT id FROM dept_tree WHERE id != #{deptId}

SELECT COUNT(1)
FROM sys_dept
WHERE dept_name = #{deptName}
AND parent_id = #{parentId}
AND deleted = 0

    AND id != #{deptId}
